import PropTypes from "prop-types";
import React, { Fragment,useState } from "react";
import MetaTags from "react-meta-tags";
import { Link } from "react-router-dom";
import { BreadcrumbsItem } from "react-breadcrumbs-dynamic";
import Tab from "react-bootstrap/Tab";
import Nav from "react-bootstrap/Nav";
import LayoutOne from "../../layouts/LayoutOne";
import Breadcrumb from "../../wrappers/breadcrumb/Breadcrumb";

const LoginRegister = ({ location }) => {
  const { pathname } = location;

  const [formData, setFormData] = useState({
    name:"",
    email:"",
    password:""

  });


  const handleLogin = () =>{

      axios({
        url:  process.env.REACT_APP_API_URL ,
        method: 'post',
        data: {
          query: `
          mutation {
            login(email: "${formData.email}", password: "${formData.password}") {
                token
                user {
                    id
                    role_id
                    name
                    phone
                    email
                    username
                    status
                    role_id
                    created_at
                    updated_at
                }
            }
        }
            `
        }
      }).then((result) => {
        
        if(result.data.data.login.user.role_id == 6){
          result.data.data.login.user.role = 'admin'
        }
  
        window.localStorage.setItem(authConfig.storageTokenKeyName, result.data.data.login.token)
        console.log(result.data)
    
        window.localStorage.setItem('userData', JSON.stringify(result.data.data.login.user))
     
      }).catch(err => {
        if (errorCallback) errorCallback(err)
      })

  }


  // const handleRegister = () =>{
    
  // }


  return (
    <Fragment>
      <MetaTags>
        <title>Flone | Login</title>
        <meta
          name="description"
          content="Compare page of flone react minimalist eCommerce template."
        />
      </MetaTags>
      <BreadcrumbsItem to={process.env.PUBLIC_URL + "/"}>Home</BreadcrumbsItem>
      <BreadcrumbsItem to={process.env.PUBLIC_URL + pathname}>
        Login Register
      </BreadcrumbsItem>
      <LayoutOne headerTop="visible">
        {/* breadcrumb */}
        <Breadcrumb />
        <div className="login-register-area pt-100 pb-100">
          <div className="container">
            <div className="row">
              <div className="col-lg-7 col-md-12 ml-auto mr-auto">
                <div className="login-register-wrapper">
                  <Tab.Container defaultActiveKey="login">
                    <Nav variant="pills" className="login-register-tab-list">
                      <Nav.Item>
                        <Nav.Link eventKey="login">
                          <h4>Login</h4>
                        </Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="register">
                          <h4>Register</h4>
                        </Nav.Link>
                      </Nav.Item>
                    </Nav>
                    <Tab.Content>
                      <Tab.Pane eventKey="login">
                        <div className="login-form-container">
                          <div className="login-register-form">
                            <form>
                              <input
                                type="text"
                                name="user-name"
                                placeholder="Username"
                                value={formData.name}
                                onChange={(event) =>
                                  setFormData({ ...formData, name: event.target.value })
                                }
                              />
                              <input
                                type="password"
                                name="user-password"
                                placeholder="Password"
                                value={formData.password}
                                onChange={(event) =>
                                  setFormData({ ...formData, password: event.target.value })
                                }
                              />
                              <div className="button-box">
                                <div className="login-toggle-btn">
                                  <input type="checkbox" />
                                  <label className="ml-10">Remember me</label>
                                  <Link to={process.env.PUBLIC_URL + "/"}>
                                    Forgot Password?
                                  </Link>
                                </div>
                                <button type="submit">
                                  <span>Login</span>
                                </button>
                              </div>
                            </form>
                          </div>
                        </div>
                      </Tab.Pane>
                      <Tab.Pane eventKey="register">
                        <div className="login-form-container">
                          <div className="login-register-form">
                            <form>
                              <input
                                type="text"
                                name="user-name"
                                placeholder="Username"
                                value={formData.name}
                                onChange={(event) =>
                                  setFormData({ ...formData, name: event.target.value })
                                }
                              />
                              <input
                                type="password"
                                name="user-password"
                                placeholder="Password"
                                value={formData.password}
                                onChange={(event) =>
                                  setFormData({ ...formData, password: event.target.value })
                                }
                              />
                              <input
                                name="user-email"
                                placeholder="Email"
                                type="email"
                                value={formData.email}
                                onChange={(event) =>
                                  setFormData({ ...formData, email: event.target.value })
                                }
                              />
                              <div className="button-box">
                                <button type="submit">
                                  <span>Register</span>
                                </button>
                              </div>
                            </form>
                          </div>
                        </div>
                      </Tab.Pane>
                    </Tab.Content>
                  </Tab.Container>
                </div>
              </div>
            </div>
          </div>
        </div>
      </LayoutOne>
    </Fragment>
  );
};

LoginRegister.propTypes = {
  location: PropTypes.object
};

export default LoginRegister;
